    string functName;
    List < string > funcParams;
    
    Common.StringFunctions.ParseIntoFuncationParams("int FuncX(Arg1, Arg2, Arg3)", out funcName, out funcParams);
        Returns => FuncX(Arg1, Arg2, Arg3)
        funcName => FuncX
        funcParams => {Arg1, Arg2, Arg3}
        
    Common.StringFunctions.ParseIntoFuncationParams("int FuncX()", out funcName, out funcParams);
        Returns => FuncX()
        funcName => FuncX
        funcParams => {}
       
    Common.StringFunctions.ParseIntoFuncationParams("int FuncX", out funcName, out funcParams);
        Returns => null
        funcName => 
        funcParams => null


    string genericName;
    List < string > genericParams;
    
    Common.StringFunctions.ParseIntoGenericTypeParams("gName< ga1, ga2 >", out genericName, out genericParams);
        Return Value => genicName< ga1, ga2 >
        genericName => gName
        genericParams => { ga1, ga2 }
        
    Common.StringFunctions.ParseIntoGenericTypeParams("funcX(a, b, c)", out genericName, out genericParams);
        Return Value => null
        genericName => 
        genericParams => null
        
    Common.StringFunctions.ParseIntoGenericTypeParams("int funcX < ga1, ga2 >(a, b, c)", out genericName, out genericParams);
        Return Value => funcX < ga1, ga2 >
        genericName => funcx
        genericParams => {ga1, ga2}

ParseIntoVariableArray("[VarA  ].[  VarB]@MyDomain.com") ==> {"vara", "varb"}
ParseIntoVariableArray("[VarA  ].[  VarB]@MyDomain.com", false) ==> {"[vara]", "[varb]"}
ParseIntoVariableArray("[VarA  ].[  VarB]@MyDomain.com", false, false) ==> {"[vara  ]", "[  varb]"}
ParseIntoVariableArray("[VarA  ].[  VarB]@MyDomain.com", false, false, false) ==> {"[VarB  ]", "[  VarB]"}

